var apis = require('./apis.js')();

var gen_employee_args = function (p_location_id,p_employee_id) {
	var args = {
			location_id:p_location_id,
			employee_id:p_employee_id,
			
	};
	return args;
}

//...
//...employees
//...
function get_location_employees_url(p_location_id) {
	var the_url = apis.get_base_url()+apis.urls.employees
	the_url = the_url.replace(apis.location_id,p_location_id);
	return the_url;
}

//...
//...employee
//...
function get_location_employee_id_url(p_args) {
	if(p_args.location_id==undefined) {
		throw new Error('missing location_id');
	}
	if(p_args.employee_id==undefined) {
		throw new Error('missing employee_id');
	}
	var the_url = apis.get_base_url()+apis.urls.employee_id;
	the_url = the_url.replace(apis.location_id,p_args.location_id);
	the_url = the_url.replace(apis.employee_id,p_args.employee_id);
	return the_url;
}



var get_location_employee_url = function(p_args) {
	return get_location_employee_id_url(p_args);
	
}

module.exports = function() {

	return {
		get_location_employees_url: get_location_employees_url,
		gen_employee_args: gen_employee_args,
		get_location_employee_url: get_location_employee_url,
		
	};
}