var apis = require('./apis.js')();

var gen_order_type_args = function (p_location_id,p_order_type_id) {
	var args = {
			location_id:p_location_id,
			order_type_id:p_order_type_id,
	};
	return args;
}

//...
//...order_types
//...
function get_location_order_types_url(p_location_id) {
	var the_url = apis.get_base_location_url(p_location_id)+apis.urls.order_types
	return the_url;
}

//...
//...order_type
//...
function get_location_order_type_id_url(p_args) {
	if(p_args.order_type_id==undefined) {
		throw new Error('missing order_type_id');
	}
	var the_url = apis.get_base_location_url(p_args.location_id)+apis.urls.order_type_id;
	the_url = the_url.replace(apis.order_type_id,p_args.order_type_id);
	return the_url;
}



var get_location_order_type_url = function(p_args) {
	return get_location_order_type_id_url(p_args);
	
}

module.exports = function() {

	return {
		get_location_order_types_url: get_location_order_types_url,
		gen_order_type_args: gen_order_type_args,
		get_location_order_type_url: get_location_order_type_url,
		
	};
}