var apis = require('./apis.js')();

var gen_payment_id_args = function (p_location_id,p_payment_id) {
	var args = {
			location_id:p_location_id,
			payment_id:p_payment_id,
			
	};
	return args;
}

//...
//...payments
//...
function get_location_payments_url(p_location_id) {
	var the_url = apis.get_base_url()+apis.urls.payments
	the_url = the_url.replace(apis.location_id,p_location_id);
	return the_url;
}

//...
//...payment
//...
function get_location_payment_id_url(p_args) {
	if(p_args.location_id==undefined) {
		throw new Error('missing location_id');
	}
	if(p_args.payment_id==undefined) {
		throw new Error('missing payment_id');
	}
	var the_url = apis.get_base_url()+apis.urls.payment_id;
	the_url = the_url.replace(apis.location_id,p_args.location_id);
	the_url = the_url.replace(apis.payment_id,p_args.payment_id);
	return the_url;
}



var get_location_payment_url = function(p_args) {
	return get_location_payment_id_url(p_args);
	
}

module.exports = function() {

	return {
		get_location_payments_url: get_location_payments_url,
		gen_payment_id_args: gen_payment_id_args,
		get_location_payment_url: get_location_payment_url,
		
	};
}