var apis = require('./apis.js')();

var gen_revenue_center_args = function (p_location_id,p_revenue_center_id) {
	var args = {
			location_id:p_location_id,
			revenue_center_id:p_revenue_center_id,
			
	};
	return args;
}

//...
//...revenue_centers
//...
function get_location_revenue_centers_url(p_location_id) {
	var the_url = apis.get_base_location_url(p_location_id)+apis.urls.revenue_centers
	return the_url;
}

//...
//...revenue_center
//...
function get_location_revenue_center_id_url(p_args) {
	if(p_args.revenue_center_id==undefined) {
		throw new Error('missing revenue_center_id');
	}
	var the_url = apis.get_base_location_url(p_args.location_id)+apis.urls.revenue_center_id;
	the_url = the_url.replace(apis.revenue_center_id,p_args.revenue_center_id);
	return the_url;
}



var get_location_revenue_center_url = function(p_args) {
	return get_location_revenue_center_id_url(p_args);
	
}

module.exports = function() {

	return {
		get_location_revenue_centers_url: get_location_revenue_centers_url,
		gen_revenue_center_args: gen_revenue_center_args,
		get_location_revenue_center_url: get_location_revenue_center_url,
		
	};
}