var apis = require('./apis.js')();

var gen_table_args = function (p_location_id,p_table_id) {
	var args = {
			location_id:p_location_id,
			table_id:p_table_id,
	};
	return args;
}

//...
//...tables
//...
function get_location_tables_url(p_location_id) {
	var the_url = apis.get_base_location_url(p_location_id)+apis.urls.tables
	return the_url;
}

//...
//...table
//...
function get_location_table_id_url(p_args) {
	if(p_args.table_id==undefined) {
		throw new Error('missing table_id');
	}
	var the_url = apis.get_base_location_url(p_args.location_id)+apis.urls.table_id;
	the_url = the_url.replace(apis.table_id,p_args.table_id);
	return the_url;
}



var get_location_table_url = function(p_args) {
	return get_location_table_id_url(p_args);
	
}

module.exports = function() {

	return {
		get_location_tables_url: get_location_tables_url,
		gen_table_args: gen_table_args,
		get_location_table_url: get_location_table_url,
		
	};
}