var apis = require('./apis.js')();

var gen_tender_type_args = function (p_location_id,p_tender_type_id) {
	var args = {
			location_id:p_location_id,
			tender_type_id:p_tender_type_id,
			
	};
	return args;
}

//...
//...tender_types
//...
function get_location_tender_types_url(p_location_id) {
	var the_url = apis.get_base_url()+apis.urls.tender_types
	the_url = the_url.replace(apis.location_id,p_location_id);
	return the_url;
}

//...
//...tender_type
//...
function get_location_tender_type_id_url(p_args) {
	if(p_args.location_id==undefined) {
		throw new Error('missing location_id');
	}
	if(p_args.tender_type_id==undefined) {
		throw new Error('missing tender_type_id');
	}
	var the_url = apis.get_base_url()+apis.urls.tender_type_id;
	the_url = the_url.replace(apis.location_id,p_args.location_id);
	the_url = the_url.replace(apis.tender_type_id,p_args.tender_type_id);
	return the_url;
}



var get_location_tender_type_url = function(p_args) {
	return get_location_tender_type_id_url(p_args);
	
}

module.exports = function() {

	return {
		get_location_tender_types_url: get_location_tender_types_url,
		gen_tender_type_args: gen_tender_type_args,
		get_location_tender_type_url: get_location_tender_type_url,
		
	};
}